function all(){return new Promise((e,t)=>{firebase.database().ref("menu/").once("value",e=>{let t=[];e.forEach(e=>{let a=e.key;console.log(a),e.forEach(e=>{let o={description:e.val().description,price:e.val().price,image:e.val().image,name:e.key,category:a};t.push(o)})}),templates.getPage("menu",t),$(".common-footer").css("display","initial")})})}function getMenuCategory(e){return new Promise((t,a)=>{let o=e.category;firebase.database().ref("menu/"+o+"/").once("value",e=>{let t=[];e.forEach(e=>{console.log(o);let a={description:e.val().description,price:e.val().price,image:e.val().image,name:e.key,category:o};t.push(a)}),templates.getPage("menu",t),$(".common-footer").css("display","initial"),"cakes"!==o&&(console.log(o),$(".cakes-title").hide())})})}import{templates}from"templates";import"bootstrap";let menuControl={all:all,getMenuCategory:getMenuCategory};export{menuControl as menuController};